mapper.select:
SELECT COUNT(*)
  FROM GJ_RS
 WHERE RS_CR = #{rs}

SELECT COUNT(*)
  FROM ${table}
 WHERE ADDR LIKE '%'||#{gu_name}||'%'

SELECT COUNT(*)
  FROM ${tb_nm}
 WHERE DEAL_YEAR = #{year}
   AND DEAL_MONTH = #{month}
   AND DEAL_PR ${mon_jeon} 0

SELECT AVG(${mon_jeon_avg})
  FROM ${tb_nm}
 WHERE DEAL_YEAR = #{year}
   AND DEAL_MONTH = #{month}
   AND DEAL_PR ${mon_jeon} 0

SELECT COUNT(*)
  FROM ${tb_nm}
 WHERE DEAL_YEAR = #{year}
   AND DEAL_PR ${mon_jeon} 0
   ANd GU = #{gu}

SELECT AVG(${mon_jeon_avg})
  FROM ${tb_nm}
 WHERE DEAL_YEAR = #{year}
   AND DEAL_PR ${mon_jeon} 0
   ANd GU = #{gu}

SELECT CORTARNO
  FROM GWANGJU_GU
 WHERE GU = #{name}
